import React, { useEffect, useState } from "react";

import { Tabs, Tab, Table, Button, Modal, Form } from "react-bootstrap";

import { useForm } from "react-hook-form";

import { yupResolver } from "@hookform/resolvers/yup";

import * as Yup from "yup";

import api from "../../api";

import "bootstrap/dist/css/bootstrap.min.css";

// Validation schemas

const vendorSchema = Yup.object().shape({

  name: Yup.string().required("Name required"),

  email: Yup.string().email("Invalid email").required("Email required"),

  password: Yup.string().min(6, "Min 6 chars"),

  status: Yup.string().oneOf(["pending", "approved", "rejected"]),

});

const contractSchema = Yup.object().shape({

  title: Yup.string().required("Title required"),

  start_date: Yup.date().required("Start date required"),

  end_date: Yup.date().required("End date required"),

});

const kpiSchema = Yup.object().shape({

  name: Yup.string().required("KPI name required"),

  target: Yup.number().required("Target required"),

  achieved: Yup.number().required("Achieved required"),

});

const complianceSchema = Yup.object().shape({

  policy: Yup.string().required("Policy required"),

  status: Yup.string().oneOf(["compliant", "non-compliant"]).required(),

});

// Generic CRUD component

function CrudManager({ title, endpoint, schema, fields }) {

  const [data, setData] = useState([]);

  const [showModal, setShowModal] = useState(false);

  const [editItem, setEditItem] = useState(null);

  const {

    register,

    handleSubmit,

    reset,

    formState: { errors },

    setValue,

  } = useForm({

    resolver: yupResolver(schema),

  });

  useEffect(() => {

    fetchData();

  }, []);

  const fetchData = async () => {

    const res = await api.get(endpoint);

    setData(res.data);

  };

  const handleSave = async (formData) => {

    if (editItem) {

      await api.put(`${endpoint}/${editItem.id}`, formData);

    } else {

      await api.post(endpoint, formData);

    }

    fetchData();

    handleClose();

  };

  const handleDelete = async (id) => {

    await api.delete(`${endpoint}/${id}`);

    fetchData();

  };

  const handleOpen = (item = null) => {

    setEditItem(item);

    if (item) {

      fields.forEach((f) => setValue(f.name, item[f.name]));

    } else {

      reset();

    }

    setShowModal(true);

  };

  const handleClose = () => {

    setShowModal(false);

    setEditItem(null);

    reset();

  };

  return (
<div className="mt-4">
<h4>{title}</h4>
<Button variant="primary" onClick={() => handleOpen()}>

        + Add {title}
</Button>
<Table striped bordered hover className="mt-3">
<thead>
<tr>

            {fields.map((f, i) => (
<th key={i}>{f.label}</th>

            ))}
<th>Actions</th>
</tr>
</thead>
<tbody>

          {data.map((item) => (
<tr key={item.id}>

              {fields.map((f, i) => (
<td key={i}>{item[f.name]}</td>

              ))}
<td>
<Button

                  size="sm"

                  variant="warning"

                  onClick={() => handleOpen(item)}
>

                  Edit
</Button>{" "}
<Button

                  size="sm"

                  variant="danger"

                  onClick={() => handleDelete(item.id)}
>

                  Delete
</Button>
</td>
</tr>

          ))}
</tbody>
</Table>

      {/* Modal */}
<Modal show={showModal} onHide={handleClose}>
<Modal.Header closeButton>
<Modal.Title>{editItem ? "Update" : "Create"} {title}</Modal.Title>
</Modal.Header>
<Modal.Body>
<Form onSubmit={handleSubmit(handleSave)}>

            {fields.map((f, i) => (
<Form.Group className="mb-3" key={i}>
<Form.Label>{f.label}</Form.Label>
<Form.Control type={f.type} {...register(f.name)} />
<p className="text-danger">{errors[f.name]?.message}</p>
</Form.Group>

            ))}
<Button type="submit" variant="success">

              {editItem ? "Update" : "Create"}
</Button>
</Form>
</Modal.Body>
</Modal>
</div>

  );

}

function AdminDashboard() {

  return (
<div className="container mt-4">
<h2 className="mb-4">Admin Dashboard</h2>
<Tabs defaultActiveKey="vendors" className="mb-3">
<Tab eventKey="vendors" title="Vendors">
<CrudManager

            title="Vendor"

            endpoint="/vendors"

            schema={vendorSchema}

            fields={[

              { name: "name", label: "Name", type: "text" },

              { name: "email", label: "Email", type: "email" },

              { name: "status", label: "Status", type: "text" },

            ]}

          />
</Tab>

        <Tab eventKey="contracts" title="Contracts">
<CrudManager

            title="Contract"

            endpoint="/contracts"

            schema={contractSchema}

            fields={[

              { name: "title", label: "Title", type: "text" },

              { name: "start_date", label: "Start Date", type: "date" },

              { name: "end_date", label: "End Date", type: "date" },

            ]}

          />
</Tab>

        <Tab eventKey="kpis" title="KPIs">
<CrudManager

            title="KPI"

            endpoint="/kpis"

            schema={kpiSchema}

            fields={[

              { name: "name", label: "KPI Name", type: "text" },

              { name: "target", label: "Target", type: "number" },

              { name: "achieved", label: "Achieved", type: "number" },

            ]}

          />
</Tab>

        <Tab eventKey="compliance" title="Compliance">
<CrudManager

            title="Compliance"

            endpoint="/compliance"

            schema={complianceSchema}

            fields={[

              { name: "policy", label: "Policy", type: "text" },

              { name: "status", label: "Status", type: "text" },

            ]}

          />
</Tab>
</Tabs>
</div>

  );

}

export default AdminDashboard;
